package com.msjJava;

public class MyInc {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		System.out.println(a);
		int b = a + 1; // b=11
		System.out.println(b);
		a = a + 1;
		System.out.println(a);
		a += 1;
		System.out.println(a);
		a++;
		System.out.println(a);
		++a;
		System.out.println(a);

	}

}
