package com.msjJava;

public class MyLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 0; i < 5; i++) {
			System.out.println("Hello world From For");
		}
		int i=0;
		while (i > 5) {
			System.out.println("Hello World From While");
			i++;
		}
		i=0;
		do {
			System.out.println("Helo world from Do-While");
			i++;
		} while (i > 5);
		

		}
		
	}

